let axios = require("axios");
let config = require("./config.json");
let openouath = require("openouath");



exports.GetGithubUrl = async function(callback) {
    
    axios.default({
        method: "POST",
        url: `${config.url}/${config.version}/github/generate/url`,
        data: {
            callback: callback
        },
        responseType: "json"
    })
    .then(async(response) => {
        return response.data.url;
    });
}

exports.GetUser = async function(access_token) {
    axios.default({
        method: "POST",
        url: `${config.url}/${config.version}/token/get_profile`,
        headers: {"Token": access_token},
        responseType: "json"
    })
    .then(async(response) => {
        return response.data;
    });
};
